import React from 'react';
import { X, AlertCircle } from 'lucide-react';

const NotificationPanel = ({ notifications, onClose }) => {
  return (
    <div className="absolute top-16 right-6 w-80 bg-white shadow-lg rounded-lg z-50">
      <div className="flex justify-between items-center px-4 py-2 border-b">
        <h3 className="text-lg font-semibold">Notifications</h3>
        <button onClick={onClose}>
          <X className="w-5 h-5 text-gray-600 hover:text-red-500" />
        </button>
      </div>

      <div className="max-h-80 overflow-y-auto">
        {notifications.length === 0 ? (
          <p className="p-4 text-sm text-gray-500">No new notifications.</p>
        ) : (
          notifications.map((note, index) => (
            <div key={index} className="flex items-start gap-2 px-4 py-3 border-b">
              <AlertCircle className="text-yellow-500 w-5 h-5 mt-1" />
              <div>
                <p className="text-sm text-gray-800">{note.message}</p>
                <p className="text-xs text-gray-400">{note.timestamp}</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default NotificationPanel;
