import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import {
  Home,
  Box,
  Users,
  Warehouse,
  CalendarCheck,
  ChevronLeft,
  ChevronRight,
} from 'lucide-react';

const Sidebar = ({ collapsed, setCollapsed }) => {
  
  const location = useLocation();

  const navLinks = [
    { path: '/dashboard', label: 'Dashboard', icon: <Home size={20} /> },
    { path: '/inventory', label: 'Inventory', icon: <Box size={20} /> },
    { path: '/employee', label: 'Employees', icon: <Users size={20} /> },
    { path: '/warehouses', label: 'Manage Warehouses', icon: <Warehouse size={20} /> },
    { path: '/attendance', label: 'Attendance Management', icon: <CalendarCheck size={20} /> },
  ];

  const linkClasses = (path) =>
    `flex items-center gap-3 px-4 py-2 rounded-md transition-all duration-200 ${
      location.pathname === path
        ? 'bg-blue-600 text-white'
        : 'text-gray-300 hover:bg-gray-700 hover:text-white'
    }`;

  return (
    <div
      className={`fixed top-0 left-0 h-screen bg-gray-900 text-white shadow-md transition-all duration-300 z-50 flex flex-col justify-between ${
        collapsed ? 'w-20' : 'w-64'
      }`}
    >
      {/* Top Section */}
      <div className="p-4">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-xl font-bold text-blue-400 transition-all duration-300">
            {collapsed ? 'FCI' : 'FCI MIS'}
          </h1>
          <button
            onClick={() => setCollapsed(!collapsed)}
            className="text-gray-400 hover:text-white ml-2"
          >
            {collapsed ? <ChevronRight size={20} /> : <ChevronLeft size={20} />}
          </button>
        </div>

        {/* Navigation */}
        <nav className="flex flex-col space-y-2">
          {navLinks.map((link) => (
            <Link
              key={link.path}
              to={link.path}
              className={linkClasses(link.path)}
            >
              {link.icon}
              {!collapsed && <span>{link.label}</span>}
            </Link>
          ))}
        </nav>
      </div>

      {/* Footer */}
      <div className="text-sm text-gray-400 px-4 pb-4 border-t border-gray-700">
        {!collapsed && (
          <>
            <p>Â© 2025 FCI Management</p>
            <p>Created by N.Themyui</p>
          </>
        )}
      </div>
    </div>
  );
};

export default Sidebar;
