import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Warehouse = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);
  const [warehouses, setWarehouses] = useState([]);

  const [formData, setFormData] = useState({
    location: '',
    capacity: ''
  });

  useEffect(() => {
    fetchWarehouses();
  }, []);

  const fetchWarehouses = () => {
    axios.get('http://127.0.0.1:8000/api/warehouses/')
      .then(res => setWarehouses(res.data))
      .catch(err => console.error('Fetch Error:', err));
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const cleanData = {
      ...formData,
      capacity: parseInt(formData.capacity, 10)
    };

    const url = isEditing
      ? `http://127.0.0.1:8000/api/warehouses/${editId}/`
      : 'http://127.0.0.1:8000/api/warehouses/';

    const method = isEditing ? axios.put : axios.post;

    method(url, cleanData)
      .then(() => {
        fetchWarehouses();
        setFormData({ location: '', capacity: '' });
        setIsEditing(false);
        setEditId(null);
      })
      .catch(err => {
        console.error('Submit Error:', err);
        if (err.response) alert(JSON.stringify(err.response.data));
      });
  };

  const handleEdit = (warehouse) => {
    setIsEditing(true);
    setEditId(warehouse.id);
    setFormData({
      location: warehouse.location,
      capacity: warehouse.capacity.toString()
    });
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this warehouse?')) {
      axios.delete(`http://127.0.0.1:8000/api/warehouses/${id}/`)
        .then(fetchWarehouses)
        .catch(err => console.error('Delete Error:', err));
    }
  };

  return (
    <div className="ml-64 p-6">
      <h2 className="text-2xl font-semibold mb-4">Warehouses</h2>

      {/* üèóÔ∏è Add/Edit Warehouse Form */}
      <form onSubmit={handleSubmit} className="mb-6 bg-white shadow p-4 rounded-md max-w-md">
        <div className="mb-4">
          <label className="block mb-1">Location:</label>
          <input
            name="location"
            type="text"
            value={formData.location}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block mb-1">Capacity:</label>
          <input
            name="capacity"
            type="number"
            value={formData.capacity}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <div className="flex space-x-4 items-center">
          <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
            {isEditing ? 'Update Warehouse' : 'Add Warehouse'}
          </button>
          {isEditing && (
            <button
              type="button"
              onClick={() => {
                setIsEditing(false);
                setEditId(null);
                setFormData({ location: '', capacity: '' });
              }}
              className="text-sm text-gray-600 underline"
            >
              Cancel Edit
            </button>
          )}
        </div>
      </form>

      {/* üì¶ Warehouse Table */}
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border mt-6">
          <thead>
            <tr className="bg-gray-200 text-left">
              <th className="py-2 px-4 border">ID</th>
              <th className="py-2 px-4 border">Location</th>
              <th className="py-2 px-4 border">Capacity</th>
              <th className="py-2 px-4 border">Actions</th>
            </tr>
          </thead>
          <tbody>
            {warehouses.map((wh) => (
              <tr key={wh.id}>
                <td className="py-2 px-4 border">{wh.id}</td>
                <td className="py-2 px-4 border">{wh.location}</td>
                <td className="py-2 px-4 border">{wh.capacity}</td>
                <td className="py-2 px-4 border">
                  <button
                    onClick={() => handleEdit(wh)}
                    className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700 mr-2"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(wh.id)}
                    className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Warehouse;
