import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Employee = () => {
  const [employees, setEmployees] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditedId] = useState(null);

  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('');

  const [formData, setFormData] = useState({
    name: '',
    designation: '',
    department: '',
    location: '',
    status: ''
  });

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = () => {
    axios.get('http://127.0.0.1:8000/api/employees/')
      .then(res => setEmployees(res.data))
      .catch(err => console.error('Error fetching employees:', err));
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const url = isEditing
      ? `http://127.0.0.1:8000/api/employees/${editId}/`
      : 'http://127.0.0.1:8000/api/employees/';
    const method = isEditing ? axios.put : axios.post;

    method(url, formData)
      .then(() => {
        fetchEmployees();
        setFormData({
          name: '',
          designation: '',
          department: '',
          location: '',
          status: ''
        });
        setIsEditing(false);
        setEditedId(null);
      })
      .catch(err => {
        console.error('Submit Error:', err);
        if (err.response) {
          alert(JSON.stringify(err.response.data));
        }
      });
  };

  const handleEdit = (employee) => {
    setIsEditing(true);
    setEditedId(employee.id);
    setFormData({
      name: employee.name,
      designation: employee.designation,
      department: employee.department,
      location: employee.location,
      status: employee.status
    });
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      axios.delete(`http://127.0.0.1:8000/api/employees/${id}/`)
        .then(fetchEmployees)
        .catch(err => console.error('Delete Error:', err));
    }
  };

  const filteredEmployees = employees.filter((emp) => {
    const matchesSearch =
      emp.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      emp.designation.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = filterStatus ? emp.status === filterStatus : true;
    return matchesSearch && matchesStatus;
  });

  return (
    <div className="ml-64 p-6">
      <h2 className="text-2xl font-semibold mb-4">Employees</h2>

      {/* Form */}
      <form onSubmit={handleSubmit} className="mb-6 bg-white shadow p-4 rounded-md max-w-md">
        {['name', 'designation', 'department', 'location'].map((field) => (
          <div className="mb-4" key={field}>
            <label className="block mb-1 capitalize">{field}:</label>
            <input
              name={field}
              type="text"
              value={formData[field]}
              onChange={handleChange}
              className="w-full border p-2 rounded"
              required
            />
          </div>
        ))}

        <div className="mb-4">
          <label className="block mb-1">Status:</label>
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          >
            <option value="">--Select--</option>
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
          </select>
        </div>

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          {isEditing ? 'Update Employee' : 'Add Employee'}
        </button>

        {isEditing && (
          <button
            type="button"
            onClick={() => {
              setIsEditing(false);
              setEditedId(null);
              setFormData({ name: '', designation: '', department: '', location: '', status: '' });
            }}
            className="ml-4 text-sm text-gray-600 underline"
          >
            Cancel Edit
          </button>
        )}
      </form>

      {/* 🔍 Search and Filter */}
      <div className="flex items-center space-x-4 mb-4">
        <input
          type="text"
          placeholder="Search by name or designation"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="border p-2 rounded w-1/3"
        />
        <select
          value={filterStatus}
          onChange={(e) => setFilterStatus(e.target.value)}
          className="border p-2 rounded"
        >
          <option value="">All Statuses</option>
          <option value="Active">Active</option>
          <option value="Inactive">Inactive</option>
        </select>
      </div>

      {/* 🧾 Table */}
      <table className="min-w-full bg-white border">
        <thead>
          <tr className="bg-gray-200 text-left">
            <th className="py-2 px-4 border">Name</th>
            <th className="py-2 px-4 border">Designation</th>
            <th className="py-2 px-4 border">Department</th>
            <th className="py-2 px-4 border">Location</th>
            <th className="py-2 px-4 border">Status</th>
            <th className="py-2 px-4 border">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredEmployees.map(emp => (
            <tr key={emp.id}>
              <td className="py-2 px-4 border">{emp.name}</td>
              <td className="py-2 px-4 border">{emp.designation}</td>
              <td className="py-2 px-4 border">{emp.department}</td>
              <td className="py-2 px-4 border">{emp.location}</td>
              <td className="py-2 px-4 border">{emp.status}</td>
              <td className="py-2 px-4 border">
                <button
                  onClick={() => handleEdit(emp)}
                  className="text-blue-600 hover:underline mr-3"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(emp.id)}
                  className="text-red-600 hover:underline"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Employee;
