import { useEffect, useState } from 'react';
import axios from 'axios';
import { PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid } from 'recharts';
import { LineChart, Line } from 'recharts';


const Dashboard = () => {
    const [summary, setSummary] = useState(null);

    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');

    const fetchDashboardData = useCallback(() => {
        const params = {};
        if (startDate) params.start_date = startDate;
        if (endDate) params.end_date = endDate;

        axios.get('http://127.0.0.1:8000/api/dashboard-summary/', { params })
            .then(res => setSummary(res.data))
            .catch(err => console.error('Dashboard Fetch Error:', err));
    }, [startDate, endDate]); // dependencies


    useEffect(() => {
        fetchDashboardData();
    }, [fetchDashboardData]);

    const handleFilter = () => {
        fetchDashboardData();
    };


    if (!summary) {
        return <div className="ml-64 p-6">Loading Dashboard...</div>;
    }

    return (
    <div className="ml-64 p-6">
      <h2 className="text-2xl font-semibold mb-6">Dashboard Summary</h2>
        <form
            onSubmit={(e) => {
                e.preventDefault(); // prevent page reload
                handleFilter();
            }}
            className="flex items-center gap-4 mb-6 bg-white p-4 rounded shadow"
        >
        <div>
            <label className="block text-sm">Start Date:</label>
            <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            className="border p-2 rounded"
            />
        </div>
        <div>
            <label className="block text-sm">End Date:</label>
            <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            className="border p-2 rounded"
            />
        </div>
        <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
            Apply Filter
        </button>

        {/* ‚ùå Cancel Button */}
        <button
            type="button"
            onClick={() => {
            setStartDate('');
            setEndDate('');
            fetchDashboardData(); // refetch full dashboard data
            }}
            className="bg-gray-400 text-white px-4 py-2 rounded hover:bg-gray-500"
        >
            Clear Filter
        </button>
        </form>


        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="bg-blue-100 p-4 rounded shadow">
            <h3 className="text-lg font-semibold">Inventory Items</h3>
            <p className="text-2xl">{summary.total_inventory}</p>
            </div>

            <div className="bg-green-100 p-4 rounded shadow">
            <h3 className="text-lg font-semibold">Warehouses</h3>
            <p className="text-2xl">{summary.total_warehouses}</p>
            </div>

            <div className="bg-yellow-100 p-4 rounded shadow">
            <h3 className="text-lg font-semibold">Employees</h3>
            <p className="text-2xl">{summary.total_employees}</p>
            </div>

            <div className="bg-red-100 p-4 rounded shadow">
            <h3 className="text-lg font-semibold">Today's Attendance</h3>
            <p className="text-md">Present: {summary.attendance_today.present}</p>
            <p className="text-md">Absent: {summary.attendance_today.absent}</p>
            <p className="text-md">Leave: {summary.attendance_today.leave}</p>
            </div>
        </div>
        {/* ‚úÖ Attendance Pie Chart */}
        <div className="mt-8 bg-white shadow p-6 rounded-lg w-full md:w-1/2">
            <h3 className="text-lg font-semibold mb-4">Attendance Overview</h3>
            <ResponsiveContainer width="100%" height={250}>
                <PieChart>
                    <Pie
                        data={summary.attendance_summary}
                        dataKey="count"
                        nameKey="status"
                        cx="50%"
                        cy="50%"
                        outerRadius={80}
                        label
                    >
                        {summary.attendance_summary.map((entry, index) => (
                            <Cell
                                key={`cell-${index}`}
                                fill={['#34D399', '#F87171', '#FBBF24'][index % 3]}
                            />
                        ))}
                    </Pie>
                    <Tooltip />
                    <Legend />
                </PieChart>
            </ResponsiveContainer>
        </div>
        {/* üì¶ Inventory Quantity Bar Chart */}
        <div className="mt-8 bg-white shadow p-6 rounded-lg w-full md:w-2/3">
            <h3 className="text-lg font-semibold mb-4">Inventory Quantities</h3>
            <ResponsiveContainer width="100%" height={300}>
                <BarChart data={summary.inventory}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="item_name" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="quantity" fill="#60A5FA" />
                </BarChart>
            </ResponsiveContainer>
        </div>
        {/* üìà Employee Activity Trends Line Chart */}
        <div className="mt-8 bg-white shadow p-6 rounded-lg w-full md:w-2/3">
            <h3 className="text-lg font-semibold mb-4">Employee Activity Trends</h3>
            <ResponsiveContainer width="100%" height={300}>
                <LineChart data={summary.employee_activities}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis allowDecimals={false} />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="count" stroke="#4F46E5" name="Activities" />
                </LineChart>
            </ResponsiveContainer>
        </div>
                
    </div>
  );
};

export default Dashboard;
