import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Attendance = () => {
  const [attendanceList, setAttendanceList] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  const [formData, setFormData] = useState({
    employee: '',
    date: '',
    status: 'Present',
  });

  useEffect(() => {
    fetchEmployees();
    fetchAttendance();
  }, []);

  const API_BASE = 'http://localhost:8000';

  const fetchEmployees = () => {
    axios
      .get(`${API_BASE}/api/employees/`)
      .then((res) => setEmployees(res.data))
      .catch((err) => console.error('Employee Fetch Error:', err));
  };

  const fetchAttendance = () => {
    axios
      .get(`${API_BASE}/api/attendance/`)
      .then((res) => setAttendanceList(res.data))
      .catch((err) => console.error('Attendance Fetch Error:', err));
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const data = {
      employee: parseInt(formData.employee),
      date: formData.date,
      status: formData.status,
    };

    const url = isEditing
      ? `${API_BASE}/api/attendance/${editId}/`
      : `${API_BASE}/api/attendance/`;

    const method = isEditing ? axios.put : axios.post;

    method(url, data)
      .then(() => {
        fetchAttendance();
        setFormData({ employee: '', date: '', status: '' });
        setIsEditing(false);
        setEditId(null);
      })
      .catch((err) => {
        console.error('Submit Error:', err);
        if (err.response) {
          alert(JSON.stringify(err.response.data));
        }
      });
  };

  const handleEdit = (att) => {
    setIsEditing(true);
    setEditId(att.id);
    setFormData({
      employee: att.employee,
      date: att.date,
      status: att.status,
    });
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this attendance record?')) {
      axios
        .delete(`${API_BASE}/api/attendance/${id}/`)
        .then(fetchAttendance)
        .catch((err) => console.error('Delete Error:', err));
    }
  };

  return (
    <div className="ml-64 p-6">
      <h2 className="text-2xl font-semibold mb-4">Attendance</h2>

      <form
        onSubmit={handleSubmit}
        className="mb-6 bg-white shadow p-4 rounded-md max-w-md"
      >
        <div className="mb-4">
          <label className="block mb-1">Employee:</label>
          <select
            name="employee"
            value={formData.employee}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          >
            <option value="">-- Select Employee --</option>
            {employees.map((emp) => (
              <option key={emp.id} value={emp.id}>
                {emp.name}
              </option>
            ))}
          </select>
        </div>

        <div className="mb-4">
          <label className="block mb-1">Date:</label>
          <input
            name="date"
            type="date"
            value={formData.date}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block mb-1">Status:</label>
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            className="w-full border p-2 rounded"
          >
            <option value="">--Select--</option>
            <option value="Present">Present</option>
            <option value="Absent">Absent</option>
            <option value="Leave">Leave</option>
          </select>
        </div>

        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          {isEditing ? 'Update Attendance' : 'Mark Attendance'}
        </button>
        {isEditing && (
          <button
            type="button"
            onClick={() => {
              setIsEditing(false);
              setEditId(null);
              setFormData({ employee: '', date: '', status: '' });
            }}
            className="ml-4 text-sm text-gray-600 underline"
          >
            Cancel Edit
          </button>
        )}
      </form>

      <table className="min-w-full bg-white border mt-6">
        <thead>
          <tr className="bg-gray-200 text-left">
            <th className="py-2 px-4 border">Employee</th>
            <th className="py-2 px-4 border">Date</th>
            <th className="py-2 px-4 border">Status</th>
            <th className="py-2 px-4 border">Actions</th>
          </tr>
        </thead>
        <tbody>
          {attendanceList.map((entry) => (
            <tr key={entry.id}>
              <td className="py-2 px-4 border">
                {employees.find((e) => e.id === entry.employee)?.name ||
                  entry.employee}
              </td>
              <td className="py-2 px-4 border">{entry.date}</td>
              <td className="py-2 px-4 border">{entry.status}</td>
              <td className="py-2 px-4 border">
                <button
                  onClick={() => handleEdit(entry)}
                  className="text-blue-600 hover:underline mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(entry.id)}
                  className="text-red-600 hover:underline"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Attendance;
