import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import {
  Home,
  Box,
  Users,
  Warehouse,
  CalendarCheck,
} from 'lucide-react';

const Sidebar = () => {
  const location = useLocation();

  const navLinks = [
    { path: '/dashboard', label: 'Dashboard', icon: <Home size={20} /> },
    { path: '/inventory', label: 'Inventory', icon: <Box size={20} /> },
    { path: '/employee', label: 'Employees', icon: <Users size={20} /> },
    { path: '/warehouses', label: 'Manage Warehouses', icon: <Warehouse size={20} /> },
    { path: '/attendance', label: 'Attendance Management', icon: <CalendarCheck size={20} /> },
  ];

  const linkClasses = (path) =>
    `flex items-center gap-3 px-4 py-3 transition duration-150 ${
      location.pathname === path
        ? 'bg-blue-600 text-white'
        : 'text-gray-300 hover:bg-gray-700 hover:text-white'
    }`;

  return (
    <div className="fixed top-16 left-0 w-64 h-screen bg-gray-900 text-white z-10">
      <h1 className="text-2xl font-bold text-blue-400 px-4 py-6">FCI MIS</h1>
      <nav className="flex flex-col">
        {navLinks.map((link) => (
          <Link key={link.path} to={link.path} className={linkClasses(link.path)}>
            {link.icon}
            <span>{link.label}</span>
          </Link>
        ))}
      </nav>
      <div className=" bottom-4 mt-auto text-sm text-gray-400 pt-6 border-t border-gray-700">
        <p>Â© 2025 FCI Management</p>
      </div>
    </div>
  );
};

export default Sidebar;
