import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import {
  Home,
  Box,
  Users,
  Warehouse,
  CalendarCheck,
  ChevronLeft,
  ChevronRight,
} from 'lucide-react';

const Sidebar = () => {
  
  const location = useLocation();

  const navLinks = [
    { path: '/dashboard', label: 'Dashboard', icon: <Home size={20} /> },
    { path: '/inventory', label: 'Inventory', icon: <Box size={20} /> },
    { path: '/employee', label: 'Employees', icon: <Users size={20} /> },
    { path: '/warehouses', label: 'Manage Warehouses', icon: <Warehouse size={20} /> },
    { path: '/attendance', label: 'Attendance Management', icon: <CalendarCheck size={20} /> },
  ];

  const linkClasses = (path) =>
    `flex items-center gap-3 px-4 py-2 rounded-md transition-all duration-200 ${
      location.pathname === path
        ? 'bg-blue-600 text-white'
        : 'text-gray-300 hover:bg-gray-700 hover:text-white'
    }`;

  return (
    <div className="w-64 h-screen fixed top-16 left-0 bg-gray-900 text-white shadow-lg">
      <div className="flex flex-col h-full p-6">
        <h1 className="text-2xl font-bold text-blue-400 mb-8">FCI MIS</h1>
        <nav className="flex flex-col space-y-2">
          {navLinks.map((link) => (
            <Link key={link.path} to={link.path} className={linkClasses(link.path)}>
              {link.icon}
              <span>{link.label}</span>
            </Link>
          ))}
        </nav>
        <div className="mt-auto text-sm text-gray-400 pt-6 border-t border-gray-700">
          <p>Â© 2025 FCI Management</p>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
