import { useEffect, useState } from 'react';
import axios from 'axios';
import {
  PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer,
  BarChart, Bar, XAxis, YAxis, CartesianGrid,
  LineChart, Line
} from 'recharts';
import { Clock, Package, Users, Clock3, Repeat2 } from 'lucide-react';

const iconMap = {
  'üì¶ Inventory Value': <Package className="w-4 h-4 text-indigo-600" />,
  'üíº Active Employees': <Users className="w-4 h-4 text-emerald-600" />,
  '‚è≥ Late Check-ins': <Clock3 className="w-4 h-4 text-yellow-600" />,
  'üîÅ Turnover Rate': <Repeat2 className="w-4 h-4 text-rose-600" />
};

const Dashboard = () => {
  const [summary, setSummary] = useState(null);
  const [activities, setActivities] = useState([]);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [topWarehouses, setTopWarehouses] = useState([]);
  const [performanceMetrics, setPerformanceMetrics] = useState([]);

  const fetchTopWarehouses = () => {
    axios.get('http://127.0.0.1:8000/api/top-warehouses/')
      .then(res => setTopWarehouses(res.data))
      .catch(err => console.error('Top Warehouses Fetch Error:', err));
  };

  const fetchDashboardData = () => {
    const params = {};
    if (startDate) params.start_date = startDate;
    if (endDate) params.end_date = endDate;

    axios.get('http://127.0.0.1:8000/api/dashboard-summary/', { params })
      .then(res => {
        setSummary(res.data);
        setPerformanceMetrics(res.data.performance_metrics || []);
      })
      .catch(err => console.error('Dashboard Fetch Error:', err));
  };

  const fetchRecentActivities = () => {
    axios.get('http://127.0.0.1:8000/api/recent-activities/')
      .then(res => setActivities(res.data))
      .catch(err => console.error('Activity Fetch Error:', err));
  };

  useEffect(() => {
    fetchDashboardData();
    fetchRecentActivities();
    fetchTopWarehouses();
  }, []);

  const handleFilter = () => fetchDashboardData();

  const formatValue = (val) => {
    if (typeof val === "number") {
      return new Intl.NumberFormat('en-IN', { maximumFractionDigits: 2 }).format(val);
    }
    return val;
  };

  if (!summary) {
    return <div className="ml-64 p-4 text-sm">Loading Dashboard...</div>;
  }

  const chartBox = "bg-white shadow-sm p-2 rounded-md text-xs max-w-md";

  return (
    <div className="pt-4 px-4 space-y-6 w-full text-sm">
      <h2 className="text-xl font-bold text-gray-800">Dashboard Summary</h2>

      <div className="bg-white p-3 rounded shadow-sm flex flex-wrap items-end gap-3 text-xs">
        <div>
          <label className="block text-gray-600">Start Date</label>
          <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} className="border p-1 rounded w-36" />
        </div>
        <div>
          <label className="block text-gray-600">End Date</label>
          <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} className="border p-1 rounded w-36" />
        </div>
        <button onClick={handleFilter} className="bg-blue-600 text-white px-2 py-1 rounded hover:bg-blue-700">Apply</button>
        <button onClick={() => { setStartDate(''); setEndDate(''); fetchDashboardData(); }} className="bg-gray-500 text-white px-2 py-1 rounded hover:bg-gray-600">Clear</button>
      </div>

      <div className="grid grid-cols-2 md:grid-cols-4 gap-3 text-xs">
        {[
          { label: 'Inventory Items', value: summary.total_inventory, color: 'blue' },
          { label: 'Warehouses', value: summary.total_warehouses, color: 'green' },
          { label: 'Employees', value: summary.total_employees, color: 'yellow' },
          { label: `Today's Attendance`, value: '', color: 'red', extra: summary.attendance_today }
        ].map(({ label, value, color, extra }, i) => (
          <div key={i} className={`bg-${color}-100 p-3 rounded shadow-sm hover:bg-${color}-200`}>
            <h3 className={`font-medium text-${color}-800`}>{label}</h3>
            {extra ? (
              <>
                <p>‚úÖ {extra.present}</p>
                <p>‚ùå {extra.absent}</p>
                <p>üõå {extra.leave}</p>
              </>
            ) : (
              <p className="text-2xl font-bold">{value}</p>
            )}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-2 lg:grid-cols-4 gap-3">
        {performanceMetrics.map((metric, idx) => (
          <div
            key={idx}
            className="relative bg-white p-3 rounded shadow-sm flex items-center space-x-2 text-xs group"
          >
            <div className="p-2 rounded-full bg-gray-100">
              {iconMap[metric.label]}
            </div>
            <div>
              <div className="text-gray-500">{metric.label}</div>
              <div className="text-lg font-bold text-gray-800">{formatValue(metric.value)}</div>
            </div>
            <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-1 \
                            bg-gray-800 text-white text-xs rounded px-2 py-1 \
                            opacity-0 group-hover:opacity-100 transition-opacity z-50">
              {tooltipText[metric.label]}
            </div>
          </div>
        ))}
      </div>

      {/* Replace chart containers with this class to reduce size */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-4">
        <div className={chartBox}>
          <h3 className="text-sm font-semibold mb-2">Attendance Overview</h3>
          <ResponsiveContainer width="100%" height={160}>
            <PieChart>
              <Pie data={summary.attendance_summary} dataKey="count" nameKey="status" cx="50%" cy="50%" outerRadius={40} label />
              <Tooltip wrapperStyle={{ fontSize: '10px' }} />
              <Legend wrapperStyle={{ fontSize: '10px' }} />
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* Additional chart containers can be updated similarly */}
        {/* Apply same class: className={chartBox} */}

      </div>
    </div>
  );
};

export default Dashboard;
