import React, { useState } from 'react';
import { Link } from 'react-router-dom';
/** code for responsive */
import { useLocation } from 'react-router-dom';
import { Menu, X, Home, Box, Users, Warehouse, CalendarCheck } from 'lucide-react';
/**end */
const Sidebar = () => {
  /**responsive */
  const [isOpen, setIsOpen] = useState(false);
  const location = useLocation();
   const navLinks = [
    { path: '/dashboard', label: 'Dashboard', icon: <Home size={18} /> },
    { path: '/inventory', label: 'Inventory', icon: <Box size={18} /> },
    { path: '/employee', label: 'Employees', icon: <Users size={18} /> },
    { path: '/warehouses', label: 'Manage Warehouses', icon: <Warehouse size={18} /> },
    { path: '/attendance', label: 'Attendance Management', icon: <CalendarCheck size={18} /> },
  ];

  const linkClasses = (path) =>
    `flex items-center gap-2 px-4 py-2 rounded-md transition-all duration-200 ${
      location.pathname === path
        ? 'bg-blue-600 text-white'
        : 'text-gray-300 hover:bg-gray-700 hover:text-white'
    }`;
    /**end */
  return (
    <>
      {/* Mobile Toggle Button */}
      <button
        className="md:hidden fixed top-4 left-4 z-50 text-white bg-gray-900 p-2 rounded shadow-lg"
        onClick={() => setIsOpen(!isOpen)}
        aria-label="Toggle Sidebar"
      >
        {isOpen ? <X size={24} /> : <Menu size={24} />}
      </button>

      {/* Sidebar */}
      <div
        className={`fixed top-0 left-0 h-full w-64 bg-gray-800 text-white shadow-lg transform transition-transform duration-300 z-40
        ${isOpen ? 'translate-x-0' : '-translate-x-full'} md:translate-x-0 md:static md:block`}
      >
        <div className="flex flex-col h-full p-6">
          <h1 className="text-2xl font-bold text-blue-400 mb-8 tracking-wide">FCI MIS</h1>

          <nav className="flex flex-col gap-2">
            {navLinks.map((link) => (
              <Link
                key={link.path}
                to={link.path}
                className={linkClasses(link.path)}
                onClick={() => setIsOpen(false)} // close on mobile
              >
                {link.icon}
                {link.label}
              </Link>
            ))}
          </nav>

          <div className="mt-auto pt-6 border-t border-gray-700 text-sm text-gray-400">
            <p>Â© 2025 FCI Management</p>
          </div>
        </div>
      </div>

      {/* Overlay on mobile */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black opacity-40 z-30 md:hidden"
          onClick={() => setIsOpen(false)}
        />
      )}
    </>
    /** 
    <div className="w-64 h-screen bg-gray-800 text-white p-4 fixed">
      <h1 className="text-2xl font-bold mb-8">FCI MIS</h1>
      <nav className="flex flex-col gap-4">
        <Link to="/home" className="hover:text-blue-400">Home</Link>
        <Link to="/dashboard" className="hover:text-blue-400">Dashboard Summary</Link>
        <Link to="/inventory" className="hover:text-blue-400">Inventory</Link>
        <Link to="/employee" className="hover:text-blue-400">Employees</Link>
        <Link to="/warehouses" className="hover:text-blue-400">Manage Warehouses</Link>
        <Link to="/attendance" className="hover:text-blue-400">Attendance Management</Link>

      </nav>
    </div>
    */
  );
};

export default Sidebar;
