import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Inventory = () => {
  const [warehouses, setWarehouses] = useState([]);
  const [inventory, setInventory] = useState([]);

  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  const [formData, setFormData] = useState({
    item_name: '',
    item_code: '',
    unit: '',
    warehouse: '',
    quantity: ''
  });

  useEffect(() => {
    fetchInventory();
    fetchWarehouses();
  }, []);

  const fetchWarehouses = () => {
  axios.get('http://127.0.0.1:8000/api/warehouses/')
    .then(res => setWarehouses(res.data))
    .catch(err => console.error('Warehouse Fetch Error:', err));
  };

  const handleEdit = (item) => {
    setIsEditing(true);
    setEditId(item.id);
    setFormData({
      item_name: item.item_name,
      item_code: item.item_code,
      unit: item.unit,
      quantity: item.quantity,
      warehouse: item.warehouse,
    });
  };
  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      axios.delete(`http://127.0.0.1:8000/api/inventory/${id}/`)
        .then(() => {
          fetchInventory();
        })
        .catch(err => {
          console.error('DELETE Error:', err);
          alert('Failed to delete item.');
        });
    }
  };


  const fetchInventory = () => {
    axios.get('http://127.0.0.1:8000/api/inventory/')
      .then(res => setInventory(res.data))
      .catch(err => console.error('GET Error:', err));
  };

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = (e) => {
      e.preventDefault();
      const cleanData = {
        ...formData,
        quantity: parseInt(formData.quantity, 10),
        warehouse: parseInt(formData.warehouse, 10),
      };

      const url = isEditing
        ? `http://127.0.0.1:8000/api/inventory/${editId}/`
        : 'http://127.0.0.1:8000/api/inventory/';
      const method = isEditing ? axios.put : axios.post;

      method(url, cleanData)
        .then(() => {
          fetchInventory();
          setFormData({ item_name: '', item_code: '', unit: '', warehouse: '', quantity: '' });
          setIsEditing(false);
          setEditId(null);
        })
        .catch(err => {
          console.error("POST/PUT Error:", err);
          if (err.response) {
            alert(JSON.stringify(err.response.data));
          }
        });
  };

  return (
    <div className="ml-64 p-6">
      <h2 className="text-2xl font-semibold mb-4">Inventory</h2>

      {/* ðŸ§¾ Add Inventory Form */}
      <form onSubmit={handleSubmit} className="mb-6 bg-white shadow p-4 rounded-md max-w-md">
        <div className="mb-4">
           <label className="block mb-1">Item Name:</label>
           <input
           name="item_name"
           type="text"
           value={formData.item_name}
           onChange={handleChange}
           className="w-full border p-2 rounded"
           required
        />
     </div>

        <div className="mb-4">
          <label className="block mb-1">Item code:</label>
          <input
            name="item_code"
            type="text"
            value={formData.item_code}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block mb-1">Unit:</label>
          <input
            name="unit"
            type="text"
            value={formData.unit}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block mb-1">Select Warehouse:</label>
          <select
            name="warehouse"
            value={formData.warehouse}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          >
            <option value="">-- Choose a warehouse --</option>
            {warehouses.map(wh => (
              <option key={wh.id} value={wh.id}>
                {wh.location}
              </option>
            ))}
          </select>

        </div>
        <div className="mb-4">
           <label className="block mb-1">Quantity:</label>
          <input
          name="quantity"
          type="number"
          value={formData.quantity}
          onChange={handleChange}
          className="w-full border p-2 rounded"
          required
       />
    </div>

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Add Item
        </button>
      </form>
      {isEditing && (
        <button
          type="button"
          onClick={() => {
            setIsEditing(false);
            setEditId(null);
            setFormData({ item_name: '', item_code: '', unit: '', warehouse: '', quantity: '' });
          }}
          className="mt-2 bg-gray-300 text-gray-800 px-3 py-1 rounded hover:bg-gray-400 transition"
        >
          Cancel Edit
        </button>
      )}


      {/* ðŸ“‹ Inventory Table */}
      <table className="min-w-full bg-white border">
        <thead>
          <tr className="bg-gray-200 text-left">
            <th className="py-2 px-4 border">Item Name</th>
            <th className="py-2 px-4 border">Item Code</th>
            <th className="py-2 px-4 border">Unit</th>
            <th className="py-2 px-4 border">Quantity</th>
            <th className="py-2 px-4 border">Warehouse</th>
          </tr>
        </thead>
        <tbody>
            {inventory.map(item => (
              <tr key={item.id}>
                <td className="py-2 px-4 border">{item.item_name}</td>
                <td className="py-2 px-4 border">{item.item_code}</td>
                <td className="py-2 px-4 border">{item.unit}</td>
                <td className="py-2 px-4 border">{item.quantity}</td>
                <td className="py-2 px-4 border">{item.warehouse_name}</td>
                <td className="py-2 px-4 border">
                  <button
                    onClick={() => handleEdit(item)}
                    className="bg-blue-400 text-white px-5 py-1 rounded hover:bg-blue-500 transition mr-4"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(item.id)}
                    className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 transition"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
         </tbody>
      </table>
    </div>
  );
};

export default Inventory;
