import { useEffect, useState } from 'react';
import axios from 'axios';
import {
  PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer,
  BarChart, Bar, XAxis, YAxis, CartesianGrid,
  LineChart, Line
} from 'recharts';
import { Clock, Package, Users, Clock3, Repeat2 } from 'lucide-react';

const iconMap = {
  'üì¶ Inventory Value': <Package className="w-6 h-6 text-indigo-600" />,
  'üíº Active Employees': <Users className="w-6 h-6 text-emerald-600" />,
  '‚è≥ Late Check-ins': <Clock3 className="w-6 h-6 text-yellow-600" />,
  'üîÅ Turnover Rate': <Repeat2 className="w-6 h-6 text-rose-600" />
};

const Dashboard = () => {
  const [summary, setSummary] = useState(null);
  const [activities, setActivities] = useState([]);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [topWarehouses, setTopWarehouses] = useState([]);
  const [performanceMetrics, setPerformanceMetrics] = useState([]);

  const fetchTopWarehouses = () => {
    axios
      .get('http://127.0.0.1:8000/api/top-warehouses/')
      .then((res) => setTopWarehouses(res.data))
      .catch((err) => console.error('Top Warehouses Fetch Error:', err));
  };

  const fetchDashboardData = () => {
    const params = {};
    if (startDate) params.start_date = startDate;
    if (endDate) params.end_date = endDate;

    axios.get('http://127.0.0.1:8000/api/dashboard-summary/', { params })
      .then(res => {
        setSummary(res.data);
        setPerformanceMetrics(res.data.performance_metrics || []);
      })
      .catch(err => console.error('Dashboard Fetch Error:', err));
  };

  const fetchRecentActivities = () => {
    axios.get('http://127.0.0.1:8000/api/recent-activities/')
      .then(res => setActivities(res.data))
      .catch(err => console.error('Activity Fetch Error:', err));
  };

  useEffect(() => {
    fetchDashboardData();
    fetchRecentActivities();
    fetchTopWarehouses();
  }, []);

  const handleFilter = () => {
    fetchDashboardData();
  };

  const formatValue = (val) => {
    if (typeof val === "number") {
      return new Intl.NumberFormat('en-IN', { maximumFractionDigits: 2 }).format(val);
    }
    return val;
  };

  if (!summary) {
    return <div className="ml-64 p-6">Loading Dashboard...</div>;
  }

  return (
    <div className="pt-4 px-6 space-y-10 w-full">
      {/* Heading */}
      <h2 className="text-3xl font-bold text-gray-800">Dashboard Summary</h2>

      {/* üîç Filter Section */}
      <div className="bg-white p-6 rounded shadow flex flex-wrap items-end gap-4">
        <div>
          <label className="block text-sm text-gray-600">Start Date</label>
          <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} className="border p-2 rounded w-44" />
        </div>
        <div>
          <label className="block text-sm text-gray-600">End Date</label>
          <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} className="border p-2 rounded w-44" />
        </div>
        <button onClick={handleFilter} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Apply Filter</button>
        <button onClick={() => { setStartDate(''); setEndDate(''); fetchDashboardData(); }} className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600">Clear Filter</button>
      </div>

      {/* üìä Summary Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-blue-100 p-6 rounded shadow hover:bg-blue-200 transition-colors">
          <h3 className="text-lg font-medium text-blue-800">Inventory Items</h3>
          <p className="text-3xl font-bold">{summary.total_inventory}</p>
        </div>
        <div className="bg-green-100 p-6 rounded shadow hover:bg-green-200 transition-colors">
          <h3 className="text-lg font-medium text-green-800">Warehouses</h3>
          <p className="text-3xl font-bold">{summary.total_warehouses}</p>
        </div>
        <div className="bg-yellow-100 p-6 rounded shadow hover:bg-yellow-200 transition-colors">
          <h3 className="text-lg font-medium text-yellow-800">Employees</h3>
          <p className="text-3xl font-bold">{summary.total_employees}</p>
        </div>
        <div className="bg-red-100 p-6 rounded shadow hover:bg-red-200 transition-colors">
          <h3 className="text-lg font-medium text-red-800">Today's Attendance</h3>
          <p>‚úÖ Present: {summary.attendance_today.present}</p>
          <p>‚ùå Absent: {summary.attendance_today.absent}</p>
          <p>üõå Leave: {summary.attendance_today.leave}</p>
        </div>
      </div>

      {/* ‚ö° Performance Metrics */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {performanceMetrics.map((metric, idx) => {
          const tooltipText = {
            'üì¶ Inventory Value': 'Total value of all inventory items (‚Çπ)',
            'üíº Active Employees': 'Employees currently marked as active',
            '‚è≥ Late Check-ins': 'Employees who checked in after 10:00 AM',
            'üîÅ Turnover Rate': 'Employees who exited this month / total √ó 100',
          }[metric.label];

          return (
            <div
              key={idx}
              className="group relative bg-white p-4 rounded-2xl shadow hover:shadow-md transition flex items-center space-x-4"
            >
              <div className="p-3 rounded-full bg-gray-100">
                {iconMap[metric.label]}
              </div>
              <div>
                <div className="text-sm text-gray-500">{metric.label}</div>
                <div className="text-2xl font-bold text-gray-800">{formatValue(metric.value)}</div>
              </div>

              {/* Tooltip */}
              <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 
                              bg-gray-800 text-white text-xs rounded px-2 py-1 
                              opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none 
                              whitespace-nowrap z-50">
                {tooltipText}
              </div>
            </div>
          );
        })}
      </div>


      {/* üìà Charts Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="bg-white shadow p-6 rounded-lg">
          <h3 className="text-lg font-semibold mb-4">Attendance Overview</h3>
          <ResponsiveContainer width="100%" height={250}>
            <PieChart>
              <Pie data={summary.attendance_summary} dataKey="count" nameKey="status" cx="50%" cy="50%" outerRadius={80} label>
                {summary.attendance_summary.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={['#34D399', '#F87171', '#FBBF24'][index % 3]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </div>

        <div className="bg-white shadow p-6 rounded-lg">
          <h3 className="text-lg font-semibold mb-4">Inventory Quantities</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={summary.inventory}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="item_name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="quantity" fill="#60A5FA" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        <div className="bg-white shadow p-6 rounded-lg lg:col-span-2">
          <h3 className="text-lg font-semibold mb-4">Employee Activity Trends</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={summary.employee_activities}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis allowDecimals={false} />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="count" stroke="#4F46E5" name="Activities" />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* üïí Recent Activities */}
      <div className="bg-white shadow p-6 rounded-lg">
        <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
          <Clock size={18} className="text-blue-600" /> Recent Activities
        </h3>
        {activities.length === 0 ? (
          <p className="text-gray-500">No recent activity found.</p>
        ) : (
          <ul className="divide-y divide-gray-200">
            {activities.map((item) => (
              <li key={item.id} className="py-3">
                <div>
                  <p className="text-sm font-medium text-gray-800">
                    {item.employee} <span className="text-gray-500">‚Äì {item.action}</span>
                  </p>
                  <p className="text-xs text-gray-500">{item.timestamp}</p>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* üèÜ Top Performing Warehouses */}
      <div className="bg-white shadow p-6 rounded-lg">
        <h3 className="text-lg font-semibold mb-4">Top Performing Warehouses</h3>
        {topWarehouses.length > 0 ? (
          <ResponsiveContainer width="100%" height={300}>
            <BarChart layout="vertical" data={topWarehouses}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis type="number" />
              <YAxis dataKey="name" type="category" />
              <Tooltip />
              <Legend />
              <Bar dataKey="turnover_rate" fill="#10B981" name="Turnover Rate" />
            </BarChart>
          </ResponsiveContainer>
        ) : (
          <p className="text-gray-500">No warehouse data available.</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
