import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import {
  Home,
  Box,
  Users,
  Warehouse,
  CalendarCheck,
} from 'lucide-react';

const Sidebar = () => {
  const location = useLocation();

  const navLinks = [
    { path: '/dashboard', label: 'Dashboard', icon: <Home size={20} /> },
    { path: '/inventory', label: 'Inventory', icon: <Box size={20} /> },
    { path: '/employee', label: 'Employees', icon: <Users size={20} /> },
    { path: '/warehouses', label: 'Manage Warehouses', icon: <Warehouse size={20} /> },
    { path: '/attendance', label: 'Attendance Management', icon: <CalendarCheck size={20} /> },
  ];

  const linkClasses = (path) =>
    `flex items-center gap-3 px-4 py-3 transition duration-150 ${
      location.pathname === path
        ? 'bg-blue-600 text-white'
        : 'text-gray-300 hover:bg-gray-700 hover:text-white'
    }`;

  return (
    <div className="w-64 fixed top-16 bottom-0 left-0 bg-gray-900 text-white shadow-lg overflow-y-auto">
  <div className="flex flex-col justify-between h-full p-6">
    {/* Navigation Section */}
    <div>
      
      <nav className="flex flex-col space-y-2">
        {navLinks.map((link) => (
          <Link key={link.path} to={link.path} className={linkClasses(link.path)}>
            {link.icon}
            <span className="text-sm">{link.label}</span>
          </Link>
        ))}
      </nav>
    </div>

    {/* Footer pinned to bottom */}
    <div className="text-sm text-gray-400 border-t border-gray-700 pt-4">
      <p>Â© 2025 FCI Management</p>
    </div>
  </div>
</div>
  );
};

export default Sidebar;
