import React, { useState } from 'react';
import { Link } from 'react-router-dom';
/** code for responsive */
import { useLocation } from 'react-router-dom';
import { Menu, X, Home, Box, Users, Warehouse, CalendarCheck } from 'lucide-react';
/**end */
const Sidebar = () => {
  /**responsive */
  
  const [isOpen, setIsOpen] = useState(false);
  const [collapsed, setCollapsed] = useState(false);
  const location = useLocation();
   const navLinks = [
    { path: '/dashboard', label: 'Dashboard', icon: <Home size={20} /> },
    { path: '/inventory', label: 'Inventory', icon: <Box size={20} /> },
    { path: '/employee', label: 'Employees', icon: <Users size={20} /> },
    { path: '/warehouses', label: 'Manage Warehouses', icon: <Warehouse size={20} /> },
    { path: '/attendance', label: 'Attendance Management', icon: <CalendarCheck size={20} /> },
  ];

  const linkClasses = (path) =>
    `flex items-center gap-3 p-3 rounded-md transition-all duration-200 ${
      location.pathname === path
        ? 'bg-blue-600 text-white'
        : 'text-gray-300 hover:bg-gray-700 hover:text-white'
    }`;
    /**end */
  return (
    <div className={`h-screen bg-gray-900 text-white p-4 fixed transition-all duration-300 ease-in-out z-40 ${collapsed ? 'w-20' : 'w-64'}`}>
      {/* Toggle Button */}
      <div className="flex justify-end mb-6">
        <button
          onClick={() => setCollapsed(!collapsed)}
          className="text-gray-400 hover:text-white"
        >
          {collapsed ? <ChevronRight size={20} /> : <ChevronLeft size={20} />}
        </button>
      </div>

      {/* Title */}
      {!collapsed && (
        <h1 className="text-2xl font-bold text-blue-400 mb-8 tracking-wide">FCI MIS</h1>
      )}

      {/* Navigation */}
      <nav className="flex flex-col space-y-2">
        {navLinks.map((link) => (
          <Link
            key={link.path}
            to={link.path}
            className={linkClasses(link.path)}
          >
            {link.icon}
            {!collapsed && <span>{link.label}</span>}
          </Link>
        ))}
      </nav>

      {/* Footer */}
      {!collapsed && (
        <div className="mt-auto text-sm text-gray-400 pt-6 border-t border-gray-700 absolute bottom-4 left-4">
          <p>Â© 2025 FCI Management</p>
        </div>
      )}
    </div>
    /** 
    <div className="w-64 h-screen bg-gray-800 text-white p-4 fixed">
      <h1 className="text-2xl font-bold mb-8">FCI MIS</h1>
      <nav className="flex flex-col gap-4">
        <Link to="/home" className="hover:text-blue-400">Home</Link>
        <Link to="/dashboard" className="hover:text-blue-400">Dashboard Summary</Link>
        <Link to="/inventory" className="hover:text-blue-400">Inventory</Link>
        <Link to="/employee" className="hover:text-blue-400">Employees</Link>
        <Link to="/warehouses" className="hover:text-blue-400">Manage Warehouses</Link>
        <Link to="/attendance" className="hover:text-blue-400">Attendance Management</Link>

      </nav>
    </div>
    */
  );
};

export default Sidebar;
