import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Inventory = () => {
  const [warehouses, setWarehouses] = useState([]);

  const [inventory, setInventory] = useState([]);
  const [formData, setFormData] = useState({
    item_name: '',
    item_code: '',
    unit: '',
    warehouse: '',
    quantity: ''
  });

  useEffect(() => {
    fetchInventory();
    fetchWarehouses();
  }, []);

  const fetchInventory = () => {
    axios.get('http://127.0.0.1:8000/api/inventory/')
      .then(res => setInventory(res.data))
      .catch(err => console.error('GET Error:', err));
  };

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = (e) => {
   e.preventDefault();
  const cleanData = {
    ...formData,
    quantity: parseInt(formData.quantity, 10),
    warehouse: parseInt(formData.warehouse, 10),

  };

  axios.post('http://127.0.0.1:8000/api/inventory/', cleanData)
    .then(() => {
      fetchInventory();
      setFormData({ item_name: '',item_code:'', unit: '', warehouse: '',quantity:''  });
    })
    .catch(err => {
    console.error("POST Error:", err);
    if (err.response) {
      console.log("Error Response from Django:", err.response.data);
      alert(JSON.stringify(err.response.data)); // Show Django error
    }
   });
  };

  return (
    <div className="ml-64 p-6">
      <h2 className="text-2xl font-semibold mb-4">Inventory</h2>

      {/* 🧾 Add Inventory Form */}
      <form onSubmit={handleSubmit} className="mb-6 bg-white shadow p-4 rounded-md max-w-md">
        <div className="mb-4">
           <label className="block mb-1">Item Name:</label>
           <input
           name="item_name"
           type="text"
           value={formData.item_name}
           onChange={handleChange}
           className="w-full border p-2 rounded"
           required
        />
     </div>

        <div className="mb-4">
          <label className="block mb-1">Item code:</label>
          <input
            name="item_code"
            type="text"
            value={formData.item_code}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block mb-1">Unit:</label>
          <input
            name="unit"
            type="text"
            value={formData.unit}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block mb-1">Select Warehouse:</label>
          <select
            name="warehouse"
            value={formData.warehouse}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          >
            <option value="">-- Choose a warehouse --</option>
            {warehouses.map(wh => (
              <option key={wh.id} value={wh.id}>
                {wh.location}
              </option>
            ))}
          </select>

        </div>
        <div className="mb-4">
           <label className="block mb-1">Quantity:</label>
          <input
          name="quantity"
          type="number"
          value={formData.quantity}
          onChange={handleChange}
          className="w-full border p-2 rounded"
          required
       />
    </div>

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Add Item
        </button>
      </form>

      {/* 📋 Inventory Table */}
      <table className="min-w-full bg-white border">
        <thead>
          <tr className="bg-gray-200 text-left">
            <th className="py-2 px-4 border">Item Name</th>
            <th className="py-2 px-4 border">Item Code</th>
            <th className="py-2 px-4 border">Unit</th>
            <th className="py-2 px-4 border">Quantity</th>
            <th className="py-2 px-4 border">Warehouse</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map(item => (
            <tr key={item.id}>
              <td className="py-2 px-4 border">{item.item_name}</td>
              <td className="py-2 px-4 border">{item.item_code}</td>
              <td className="py-2 px-4 border">{item.unit}</td>
              <td className="py-2 px-4 border">{item.quantity}</td>
              <td className="py-2 px-4 border">{item.warehouse_name || `Warehouse ${item.warehouse}`}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Inventory;
