import React, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import Sidebar from './Sidebar';
import { User, LogOut } from 'lucide-react';

const Layout = () => {
  const [collapsed, setCollapsed] = useState(false);

  return (
    <div className="flex flex-col h-screen">
      {/* Header */}
      <header className="fixed top-0 left-0 right-0 h-16 bg-white shadow-md z-50 flex items-center justify-between px-6">
        <div className="text-xl font-bold text-blue-600">MIS</div>
        <div className="flex items-center gap-4 text-gray-700">
          <User />
          <button className="flex items-center gap-1 text-sm hover:text-red-500">
            <LogOut size={18} />
            Logout
          </button>
        </div>
      </header>

      {/* Main Body: Sidebar + Content */}
      <div className="flex flex-1 pt-16">
        <Sidebar collapsed={collapsed} setCollapsed={setCollapsed} />

        <main
          className={`transition-all duration-300 w-full ${
            collapsed ? 'ml-20' : 'ml-64'
          } p-6`}
        >
          <div className="max-w-6xl mx-auto">
            <Outlet />
          </div>
        </main>
      </div>
    </div>
  );
};

export default Layout;
