import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Menu, X } from 'lucide-react'; // Optional: You can use any icon lib

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      {/* Toggle Button (Mobile) */}
      <button
        className="md:hidden fixed top-4 left-4 z-50 text-white bg-gray-800 p-2 rounded"
        onClick={() => setIsOpen(!isOpen)}
      >
        {isOpen ? <X size={24} /> : <Menu size={24} />}
      </button>

      {/* Sidebar */}
      <div
        className={`fixed top-0 left-0 h-full bg-gray-800 text-white p-4 w-64 z-40 transform transition-transform duration-300 ease-in-out
        ${isOpen ? 'translate-x-0' : '-translate-x-full'} md:translate-x-0 md:static md:block`}
      >
        <h1 className="text-2xl font-bold mb-8">FCI MIS</h1>
        <nav className="flex flex-col gap-4">
          <Link to="/dashboard" className="hover:text-blue-400">Dashboard</Link>
          <Link to="/inventory" className="hover:text-blue-400">Inventory</Link>
          <Link to="/employee" className="hover:text-blue-400">Employees</Link>
          <Link to="/warehouses" className="hover:text-blue-400">Manage Warehouses</Link>
          <Link to="/attendance" className="hover:text-blue-400">Attendance Management</Link>
        </nav>
      </div>
    </>
  );
};

export default Sidebar;
