import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Inventory = () => {
  const [inventory, setInventory] = useState([]);
  const [formData, setFormData] = useState({
    item_name: '',
    warehouse: '',
    quantity: ''
  });

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = () => {
    axios.get('http://127.0.0.1:8000/api/inventory/')
      .then(res => setInventory(res.data))
      .catch(err => console.error('GET Error:', err));
  };

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitting:', formData);
    axios.post('http://127.0.0.1:8000/api/inventory/', formData)
      .then(() => {
        fetchInventory();            // Refresh list
        setFormData({ item_name: '', warehouse: '', quantity: '' });  // Clear form
      })
      .catch(err => console.error('POST Error:', err));
  };

  return (
    <div className="ml-64 p-6">
      <h2 className="text-2xl font-semibold mb-4">Inventory</h2>

      {/* ðŸ§¾ Add Inventory Form */}
      <form onSubmit={handleSubmit} className="mb-6 bg-white shadow p-4 rounded-md max-w-md">
        <div className="mb-4">
          <label className="block mb-1">Item Name:</label>
          <input
            name="item_name"
            type="text"
            value={formData.item_name}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block mb-1">Warehouse:</label>
          <input
            name="warehouse"
            type="text"
            value={formData.warehouse}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block mb-1">Quantity:</label>
          <input
            name="quantity"
            type="number"
            value={formData.quantity}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Add Item
        </button>
      </form>

      {/* ðŸ“‹ Inventory Table */}
      <table className="min-w-full bg-white border">
        <thead>
          <tr className="bg-gray-200 text-left">
            <th className="py-2 px-4 border">ID</th>
            <th className="py-2 px-4 border">Item</th>
            <th className="py-2 px-4 border">Warehouse</th>
            <th className="py-2 px-4 border">Quantity</th>
          </tr>
        </thead>
        <tbody>
          {inventory.map(item => (
            <tr key={item.id}>
              <td className="py-2 px-4 border">{item.id}</td>
              <td className="py-2 px-4 border">{item.item_name}</td>
              <td className="py-2 px-4 border">{item.warehouse}</td>
              <td className="py-2 px-4 border">{item.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Inventory;
