import { useEffect, useState } from 'react';
import axios from 'axios';
import {
  PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer,
  BarChart, Bar, XAxis, YAxis, CartesianGrid,
  LineChart, Line
} from 'recharts';
import { Clock, Package, Users, Clock3, Repeat2 } from 'lucide-react';

const iconMap = {
  'üì¶ Inventory Value': <Package className="w-4 h-4 text-indigo-600" />,
  'üíº Active Employees': <Users className="w-4 h-4 text-emerald-600" />,
  '‚è≥ Late Check-ins': <Clock3 className="w-4 h-4 text-yellow-600" />,
  'üîÅ Turnover Rate': <Repeat2 className="w-4 h-4 text-rose-600" />
};

const Dashboard = () => {
  const [summary, setSummary] = useState(null);
  const [activities, setActivities] = useState([]);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [topWarehouses, setTopWarehouses] = useState([]);
  const [performanceMetrics, setPerformanceMetrics] = useState([]);

  const fetchTopWarehouses = () => {
    axios
      .get('http://127.0.0.1:8000/api/top-warehouses/')
      .then((res) => setTopWarehouses(res.data))
      .catch((err) => console.error('Top Warehouses Fetch Error:', err));
  };

  const fetchDashboardData = () => {
    const params = {};
    if (startDate) params.start_date = startDate;
    if (endDate) params.end_date = endDate;

    axios.get('http://127.0.0.1:8000/api/dashboard-summary/', { params })
      .then(res => {
        setSummary(res.data);
        setPerformanceMetrics(res.data.performance_metrics || []);
      })
      .catch(err => console.error('Dashboard Fetch Error:', err));
  };

  const fetchRecentActivities = () => {
    axios.get('http://127.0.0.1:8000/api/recent-activities/')
      .then(res => setActivities(res.data))
      .catch(err => console.error('Activity Fetch Error:', err));
  };

  useEffect(() => {
    fetchDashboardData();
    fetchRecentActivities();
    fetchTopWarehouses();
  }, []);

  const handleFilter = () => {
    fetchDashboardData();
  };

  const formatValue = (val) => {
    if (typeof val === "number") {
      return new Intl.NumberFormat('en-IN', { maximumFractionDigits: 2 }).format(val);
    }
    return val;
  };

  if (!summary) {
    return <div className="ml-64 p-4 text-sm">Loading Dashboard...</div>;
  }

  return (
    <div className="pt-2 px-4 space-y-6 w-full text-sm">
      <h2 className="text-xl font-semibold text-gray-800">Dashboard Summary</h2>

      {/* Filters */}
      <div className="bg-white p-4 rounded shadow flex flex-wrap items-end gap-2 text-sm">
        <div>
          <label className="block text-gray-600">Start Date</label>
          <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} className="border p-1 rounded w-32" />
        </div>
        <div>
          <label className="block text-gray-600">End Date</label>
          <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} className="border p-1 rounded w-32" />
        </div>
        <button onClick={handleFilter} className="bg-blue-600 text-white px-2 py-1 rounded hover:bg-blue-700">Apply</button>
        <button onClick={() => { setStartDate(''); setEndDate(''); fetchDashboardData(); }} className="bg-gray-500 text-white px-2 py-1 rounded hover:bg-gray-600">Clear</button>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-3">
        <div className="bg-blue-100 p-3 rounded shadow">
          <h3 className="text-sm font-medium text-blue-800">Inventory Items</h3>
          <p className="text-xl font-bold">{summary.total_inventory}</p>
        </div>
        <div className="bg-green-100 p-3 rounded shadow">
          <h3 className="text-sm font-medium text-green-800">Warehouses</h3>
          <p className="text-xl font-bold">{summary.total_warehouses}</p>
        </div>
        <div className="bg-yellow-100 p-3 rounded shadow">
          <h3 className="text-sm font-medium text-yellow-800">Employees</h3>
          <p className="text-xl font-bold">{summary.total_employees}</p>
        </div>
        <div className="bg-red-100 p-3 rounded shadow">
          <h3 className="text-sm font-medium text-red-800">Today's Attendance</h3>
          <p>‚úÖ {summary.attendance_today.present}</p>
          <p>‚ùå {summary.attendance_today.absent}</p>
          <p>üõå {summary.attendance_today.leave}</p>
        </div>
      </div>

      {/* Performance Metrics */}
      <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-3">
        {performanceMetrics.map((metric, idx) => {
          const tooltipText = {
            'üì¶ Inventory Value': 'Total value of inventory (‚Çπ)',
            'üíº Active Employees': 'Currently employed and active',
            '‚è≥ Late Check-ins': 'Checked in after 10:00 AM',
            'üîÅ Turnover Rate': 'Exited this month / total',
          }[metric.label];

          return (
            <div key={idx} className="group relative bg-white p-3 rounded shadow flex items-center space-x-2">
              <div className="p-2 bg-gray-100 rounded-full">{iconMap[metric.label]}</div>
              <div>
                <div className="text-xs text-gray-600">{metric.label}</div>
                <div className="text-lg font-bold">{formatValue(metric.value)}</div>
              </div>
              <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-1 bg-gray-800 text-white text-xs rounded px-2 py-1 opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none z-50">
                {tooltipText}
              </div>
            </div>
          );
        })}
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
        <div className="bg-white shadow p-3 rounded">
          <h3 className="text-sm font-semibold mb-2">Attendance Overview</h3>
          <ResponsiveContainer width="100%" height={180}>
            <PieChart>
              <Pie data={summary.attendance_summary} dataKey="count" nameKey="status" cx="50%" cy="50%" outerRadius={60} label>
                {summary.attendance_summary.map((_, index) => (
                  <Cell key={index} fill={['#34D399', '#F87171', '#FBBF24'][index % 3]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </div>

        <div className="bg-white shadow p-3 rounded">
          <h3 className="text-sm font-semibold mb-2">Inventory Quantities</h3>
          <ResponsiveContainer width="100%" height={180}>
            <BarChart data={summary.inventory}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="item_name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="quantity" fill="#60A5FA" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        <div className="bg-white shadow p-3 rounded md:col-span-2">
          <h3 className="text-sm font-semibold mb-2">Employee Activity Trends</h3>
          <ResponsiveContainer width="100%" height={200}>
            <LineChart data={summary.employee_activities}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis allowDecimals={false} />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="count" stroke="#4F46E5" name="Activities" />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Recent Activities */}
      <div className="bg-white shadow p-3 rounded">
        <h3 className="text-sm font-semibold mb-2 flex items-center gap-1">
          <Clock size={16} className="text-blue-600" /> Recent Activities
        </h3>
        {activities.length === 0 ? (
          <p className="text-gray-500 text-xs">No recent activity found.</p>
        ) : (
          <ul className="divide-y divide-gray-200">
            {activities.map((item) => (
              <li key={item.id} className="py-2">
                <p className="text-sm text-gray-800">{item.employee} ‚Äì <span className="text-gray-500">{item.action}</span></p>
                <p className="text-xs text-gray-500">{item.timestamp}</p>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Top Warehouses */}
      <div className="bg-white shadow p-3 rounded">
        <h3 className="text-sm font-semibold mb-2">Top Performing Warehouses</h3>
        {topWarehouses.length > 0 ? (
          <ResponsiveContainer width="100%" height={200}>
            <BarChart layout="vertical" data={topWarehouses}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis type="number" />
              <YAxis dataKey="name" type="category" />
              <Tooltip />
              <Legend />
              <Bar dataKey="turnover_rate" fill="#10B981" name="Turnover Rate" />
            </BarChart>
          </ResponsiveContainer>
        ) : (
          <p className="text-gray-500 text-xs">No warehouse data available.</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
