import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Attendance = () => {
  const [attendanceList, setAttendanceList] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [formData, setFormData] = useState({
    employee: '',
    date: '',
    status: 'Present'
  });

  useEffect(() => {
    fetchEmployees();
    fetchAttendance();
  }, []);

  const fetchEmployees = () => {
    axios.get('http://127.0.0.1:8000/api/employees/')
      .then(res => setEmployees(res.data))
      .catch(err => console.error('Employee Fetch Error:', err));
  };

  const fetchAttendance = () => {
    axios.get('http://127.0.0.1:8000/api/attendance/')
      .then(res => setAttendanceList(res.data))
      .catch(err => console.error('Attendance Fetch Error:', err));
  };

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://127.0.0.1:8000/api/attendance/', formData)
      .then(() => {
        fetchAttendance();
        setFormData({ employee: '', date: '', status: 'Present' });
      })
      .catch(err => {
        console.error("POST Error:", err);
        if (err.response) {
          alert(JSON.stringify(err.response.data));
        }
      });
  };

  return (
    <div className="ml-64 p-6">
      <h2 className="text-2xl font-semibold mb-4">Attendance</h2>

      <form onSubmit={handleSubmit} className="mb-6 bg-white shadow p-4 rounded-md max-w-md">
        <div className="mb-4">
          <label className="block mb-1">Employee:</label>
          <select
            name="employee"
            value={formData.employee}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          >
            <option value="">-- Select Employee --</option>
            {employees.map(emp => (
              <option key={emp.id} value={emp.id}>
                {emp.name}
              </option>
            ))}
          </select>
        </div>

        <div className="mb-4">
          <label className="block mb-1">Date:</label>
          <input
            name="date"
            type="date"
            value={formData.date}
            onChange={handleChange}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block mb-1">Status:</label>
          <select
            name="status"
            value={formData.status}
            onChange={handleChange}
            className="w-full border p-2 rounded"
          >
            <option value="Present">Present</option>
            <option value="Absent">Absent</option>
            <option value="Leave">Leave</option>
          </select>
        </div>

        <button type="submit" className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
          Mark Attendance
        </button>
      </form>

      <table className="min-w-full bg-white border mt-6">
        <thead>
          <tr className="bg-gray-200 text-left">
            <th className="py-2 px-4 border">Employee</th>
            <th className="py-2 px-4 border">Date</th>
            <th className="py-2 px-4 border">Status</th>
          </tr>
        </thead>
        <tbody>
          {attendanceList.map(entry => (
            <tr key={entry.id}>
              <td className="py-2 px-4 border">{employees.find(e => e.id === entry.employee)?.name || entry.employee}</td>
              <td className="py-2 px-4 border">{entry.date}</td>
              <td className="py-2 px-4 border">{entry.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Attendance;
