// src/components/Header.js
import { LogOut, UserCircle ,Bell} from 'lucide-react';
import React, { useState } from 'react';
import axios from 'axios';
import NotificationPanel from './NotificationPanel';

const Header = () => {
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState([]);

  const toggleNotifications = async () => {
    if (!showNotifications) {
      try {
        const res = await axios.get('http://127.0.0.1:8000/api/alerts/');
        setNotifications(res.data);
      } catch (err) {
        console.error('Error fetching alerts:', err);
      }
    }
    setShowNotifications(!showNotifications);
  };

  return (
    <header className="fixed top-0 left-0 right-0 h-16 bg-gray-800 text-white flex items-center justify-between px-6 z-50 shadow-md">
      {/* Left: Logo */}
      <div className="flex items-center space-x-2">
        <img src="/logo192.png" alt="MIS Logo" className="h-8 w-8" /> {/* or use any logo */}
        <h1 className="text-xl font-bold tracking-wide">FCI MIS</h1>
      </div>

      {/* Alert notification */}
      <div className="relative">
        <button 
          onClick={toggleNotifications} className="relative p-2 hover:bg-grey-300 rounded-full transition-colors">
          <Bell className="w-6 h-6 text-yellow-500" />
          {/* ðŸ”´ Notification dot */}
          {notifications.length > 0 && (
            <span className="absolute top-1 right-1 inline-block w-2 h-2 bg-red-600 rounded-full animate-pulse"></span>
          )}
        </button>

        {showNotifications && (
          <NotificationPanel
            notifications={notifications}
            onClose={() => setShowNotifications(false)}
          />
        )}
      </div>
      {/* Right: User info & Logout */}
      <div className="flex items-center space-x-4">
        <UserCircle size={24} className="text-gray-300" />
        <button className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-sm flex items-center">
          <LogOut size={16} className="mr-1" />
          Logout
        </button>
      </div>
      
    </header>
  );
};

export default Header;
