// src/components/Header.js
import React, { useState, useEffect } from 'react';
import { Bell, UserCircle, LogOut } from 'lucide-react';
import axios from 'axios';
import { toast } from 'react-toastify';
import NotificationPanel from './NotificationPanel';

const Header = () => {
  const API_BASE = 'http://localhost:8000'; // âœ… for Electron offline mode

  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState([]);

  const fetchAlerts = async () => {
    try {
      const res = await axios.get(`${API_BASE}/api/alerts/`);
      return res.data;
    } catch (err) {
      console.error('Error fetching alerts:', err);
      return [];
    }
  };

  const toggleNotifications = async () => {
    if (!showNotifications) {
      const data = await fetchAlerts();
      setNotifications(data);
    }
    setShowNotifications(!showNotifications);
  };

  useEffect(() => {
    let isMounted = true;

    const interval = setInterval(async () => {
      const data = await fetchAlerts();
      if (isMounted && data.length > notifications.length) {
        const newCount = data.length - notifications.length;
        toast.info(`ðŸ“¢ You have ${newCount} new alert${newCount > 1 ? 's' : ''}!`);
        setNotifications(data);
      }
    }, 60000); // 60s polling

    return () => {
      isMounted = false;
      clearInterval(interval);
    };
  }, [notifications]);

  return (
    <header className="fixed top-0 left-0 right-0 h-16 bg-gray-800 text-white flex items-center justify-between px-6 z-50 shadow-md">
      {/* Logo */}
      <div className="flex items-center space-x-2">
        <img src="/logo192.png" alt="MIS Logo" className="h-8 w-8" />
        <h1 className="text-xl font-bold tracking-wide">FCI MIS</h1>
      </div>

      {/* Right: Notification & Profile */}
      <div className="flex items-center space-x-4">
        {/* ðŸ”” Bell Icon */}
        <div className="relative">
          <button
            onClick={toggleNotifications}
            className="relative p-2 hover:bg-yellow-50 rounded-full transition-colors"
          >
            <Bell className="w-6 h-6 text-yellow-500" />
            {notifications.length > 0 && (
              <span className="absolute -top-1 -right-1 inline-flex items-center justify-center px-1.5 py-0.5 text-xs font-semibold text-white bg-red-600 rounded-full">
                {notifications.length}
              </span>
            )}
          </button>

          {showNotifications && (
            <NotificationPanel
              notifications={notifications}
              onClose={() => setShowNotifications(false)}
            />
          )}
        </div>

        {/* User Icon & Logout */}
        <UserCircle size={24} className="text-gray-300" />
        <button className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-sm flex items-center">
          <LogOut size={16} className="mr-1" />
          Logout
        </button>
      </div>
    </header>
  );
};

export default Header;
